package nhg;


import org.bukkit.Bukkit;
import org.bukkit.GameMode;
import org.bukkit.World;
import org.bukkit.entity.EntityType;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.entity.EntityDamageByEntityEvent;
import org.bukkit.event.entity.EntityDamageEvent;
import org.bukkit.event.player.PlayerJoinEvent;
import org.bukkit.event.player.PlayerMoveEvent;
import org.bukkit.event.player.PlayerQuitEvent;
import org.bukkit.plugin.java.JavaPlugin;
import org.bukkit.scheduler.BukkitScheduler;



public class NHG extends JavaPlugin implements Listener {

    private BukkitScheduler scheduler = Bukkit.getServer().getScheduler();

    private PlayerHandler playerHandler;
    private GameHandler gameHandler;
    private CommandHandler commandHandler;
    private YamlDataHandler dataHandler;
    private BorderManager borderManager;

    private World gameWorld = Bukkit.getWorld("world");

    @Override
    public void onEnable() {

        borderManager = new BorderManager(this);
        gameHandler = new GameHandler(this);
        playerHandler = new PlayerHandler(this);
        commandHandler = new CommandHandler(this);
        dataHandler = new YamlDataHandler(this);

        Bukkit.getPluginManager().registerEvents(this, this);

        for(Player p : Bukkit.getServer().getOnlinePlayers()) {
            // kick all online players (for reload) - cheap way to fix a bug cuz im lazy
            p.kickPlayer("i didnt make this /rl compatible womp womp");
        }

        playerHandler.serverLoad();

        dataHandler.createDirectoryIfMissing("plugins/NHG");
        dataHandler.copyTemplateIfMissing("gameData.yml", "plugins/NHG/gameData");
        dataHandler.addFile("data", "plugins/ArcticValentines/gameData.yml");
        dataHandler.loadFileYAML("data");

        dataHandler.initializeScheduledUpdate(1000, "data");

        borderManager.reset();



        if(Bukkit.getPluginManager().getPlugin("PlaceholderAPI") != null) {
              new NHGPlaceholders(this).register();
        }


        // SCHEDULER //
        scheduler.scheduleSyncRepeatingTask(this, new Runnable() {
            @Override
            public void run() {

                gameHandler.tick();
                playerHandler.tick();
                borderManager.tick();
            }
        }, 0L, 0L);
        
    }

    @Override
    public void onDisable() {
        dataHandler.update("data");

    }

    /**
     * For some reason i cannot set the gamemode immediately upon player joining, i think this might be a plugin conflict with multiverse or something
     * either way this will set the gamemode after a couple tick delay.
     * @param gamemode
     * @param player
     */
    public void setGamemode(GameMode gamemode, Player player) {
        scheduler.scheduleSyncDelayedTask(this, new Runnable() {
            @Override
            public void run() {

                player.setGameMode(gamemode);
            }
        }, 10L);
    }

    public GameHandler getGameHandler() {
        return this.gameHandler;
    }

    public PlayerHandler getPlayerHandler() {
        return this.playerHandler;
    }

    public World getGameWorld() {
        return(this.gameWorld);
    }

    public BorderManager getBorderManager() {
        return this.borderManager;
    }

    // TODO: Dedicated event handler
    // EVENTS: PASS THESE EVENTS TO RESPECTIVE CLASSES //

    @EventHandler
    public void onPlayerJoin(PlayerJoinEvent e) {
        playerHandler.joinedPlayer(e.getPlayer());
    }

    @EventHandler
    public void onPlayerLeave(PlayerQuitEvent e) {
        playerHandler.quitPlayer(e.getPlayer());
    }

    @EventHandler
    public void onPlayerMove(PlayerMoveEvent e) {
        if(gameHandler.getGamePaused() && playerHandler.getGamePlayer(e.getPlayer().getUniqueId()).getInGame()) {
            e.setCancelled(true);
        }
    }

    @EventHandler
    public void onEntityDamage(EntityDamageEvent e) {

        if(e.getEntityType() == EntityType.PLAYER && gameHandler.getGamePaused()) {
            e.setCancelled(true);
        }
    }

    @EventHandler void onPlayerFight(EntityDamageByEntityEvent e) {
        if(e.getEntity() instanceof Player && e.getDamager() instanceof Player) {
            // player attacked player.
            this.playerHandler.playerAttackedPlayer((Player) e.getEntity(), (Player) e.getDamager());
        }
    }
}

